import { React,useState } from 'react';
import './App.css';
import axios from 'axios';
import moment from 'moment';

function App() {

  const [list,setlist]=useState([])
  const[MRDNum,setMRDNum]=useState()


  const getBill=() =>{

    let params={
      "MRD":MRDNum
    }
axios.get("http://localhost:9001/get",{params}).then((Responce) => {


setlist(Responce.data)  
// console.log(Response)
console.log ("got the data")



}).catch((err) => {
  console.log(err)
});




  }




  return (
    <div className="App">

      <div className="main3">

      <h3>IP Status Report</h3>

      <label>MRD: </label>

      <input
      className='text'
        type={"text"}
        onChange={(event) => {
          setMRDNum(event.target.value);
          // console.log(event.target.value)
        }}
      />
        <h6>MARKED FOR DISCHARGE</h6>

      <button className="btn" onClick={getBill}>
        get details
      </button>
    </div>

      <table className='table' border={5} cellPadding={10}>
        <thead>
        <tr className='tablerow'>
          <th>Patient_id</th>
          <th>Patient_Name</th>
          <th>Visit_id</th>
          <th>Visit_code</th>
          <th>Last_Modified_By</th>
          <th>User_Name</th>
          <th>status_date_time</th>
          <th>Status</th>
          <th>Description</th>
          <th>Bed_no</th>
          <th>Ward</th>
        </tr>
        </thead>


          <tbody>
          {list.map((val)=>{

            return(
              <tr className='tabrow'>
                <td>{val.patient_id}</td>

                <td>{val.full_name}</td>

                <td>{val.visit_id}</td>

                <td>{val.visit_code}</td>

                <td>{val.last_modified_by}</td>

                <td>{val.user_name}</td>

                <td>{moment(val.status_date_time).format('MMMM Do YYYY, h:mm:ss a')}</td>

                <td>{val.status}</td>

                <td>{val.description}</td>

                <td>{val.bed_no}</td>

                <td>{val.name}</td>
              </tr>
            )
          })}
          </tbody>

       </table>



</div>
  );
}

export default App;
